var categoryManager={initialize:function(){$("#confirm-modal").on("show.bs.modal",function(a){var o=$(a.relatedTarget),r=o.data("category"),e=$(this);e.find(".btn-primary").click(function(){categoryManager.removeTag(r)})})},removeTag:function(a){$.ajax({type:"DELETE",url:"/author/categories/"+a,success:function(){location.reload()},error:function(a){alert(a)}})}};