var tagManager={initialize:function(){$("#confirm-modal").on("show.bs.modal",function(a){var n=$(a.relatedTarget),t=n.data("tag"),o=$(this);o.find(".btn-primary").click(function(){tagManager.removeTag(t)})})},removeTag:function(a){$.ajax({type:"DELETE",url:"/author/tags/"+a,success:function(){location.reload()},error:function(a){alert(a)}})}};