var tagManager={initialize:function(){$("#confirm-modal").on("show.bs.modal",function(a){var n=$(a.relatedTarget).data("tag");$(this).find(".btn-primary").click(function(){tagManager.removeTag(n)})})},removeTag:function(a){$.ajax({type:"DELETE",url:"/author/tags/"+a,success:function(){location.reload()},error:function(a){alert(a)}})}};